import base64, struct
bmp_data = base64.b64decode('')
def bmp_info(data):

    bmp_r=struct.unpack('<ccIIIIIIHHx', data)
    return {
        'width': bmp_r[-4],
        'height': bmp_r[-3],
        'color': bmp_r[-1]
    }

bi = bmp_info(bmp_data)
if bi['width'] == 28:
    print('宽度正确')
if bi['height'] == 10:
    print('高度正确')
if bi['color'] == 16:
    print('颜色正确')